// === Par√¢metros ===
const THRESHOLD = 80;           // m√≠nimo para aprovar
const MAX_ITENS = 30;           // m√°ximo a retornar
const DECAY_HALF_LIFE_H = 48;   // meia-vida da rec√™ncia (horas)
const DEDUPE_SIMILARITY = 0.85; // similaridade p/ dedupe (0..1)
const KEEP_SCORE_IN_OUTPUT = false; // true s√≥ p/ debug

// === Dicion√°rios de relev√¢ncia (tema tribut√°rio focado em empresas) ===
const KW = {
  forte: [
    // B√°sicos
    'tributo','tributos','tribut√°rio','tribut√°ria','tribut√°rias','tribut√°rios',
    'imposto','impostos',

    // Impostos espec√≠ficos (PJ)
    'icms','ipi','iss','irpj','csll','pis','cofins','iof','cide',

    // Reforma e mudan√ßas
    'reforma tribut√°ria','sistema tribut√°rio','c√≥digo tribut√°rio','ctn',

    // Fisco e fiscaliza√ß√£o
    'fisco','arrecada√ß√£o','fiscaliza√ß√£o','autua√ß√£o','execu√ß√£o fiscal',
    'notifica√ß√£o fiscal','cobran√ßa tribut√°ria',

    // Planejamento e elis√£o
    'planejamento tribut√°rio','elis√£o fiscal','evas√£o fiscal','sonega√ß√£o',

    // Incentivos e cr√©ditos
    'incentivo fiscal','incentivos fiscais','benef√≠cio fiscal','benef√≠cios fiscais',
    'cr√©dito tribut√°rio','compensa√ß√£o tribut√°ria','recupera√ß√£o de cr√©dito',

    // √ìrg√£os e julgamentos
    'carf','stf','stj','receita federal','sefaz','pgfn',

    // Regimes e enquadramentos
    'simples nacional','lucro presumido','lucro real','lucro arbitrado',

    // Obriga√ß√µes acess√≥rias
    'sped','efd','ecf','dctf','dirf','perdcomp','nf-e','nota fiscal',
    'obriga√ß√µes acess√≥rias','bloco k'
  ],
  medio: [
    'contabilidade','contador','auditoria','compliance',
    'fraude fiscal','due diligence','jurisprud√™ncia','ac√≥rd√£o',
    'consulta tribut√°ria','instru√ß√£o normativa','solu√ß√£o de consulta'
  ],
  fracos: [
    'jogos','game','celebridade','famoso','esporte','entretenimento',
    'show','cantor','cantora','filme','s√©rie','bbb','futebol',
    'meme','tiktok','gadget','smartphone'
  ],
  brasil: [
    'brasil','receita federal','carf','stf','stj',
    'sefaz','pgfn','procuradoria da fazenda nacional',
    'governo federal','congresso','c√¢mara','senado'
  ],
  contextoEco: [
    'tributa√ß√£o','carga tribut√°ria empresarial','arrecada√ß√£o','or√ßamento',
    'd√©ficit fiscal','responsabilidade fiscal','economia','pib',
    'reforma','pol√≠tica fiscal','isen√ß√£o','ren√∫ncia fiscal',
    'impacto tribut√°rio nos neg√≥cios','regime tribut√°rio empresas'
  ],
  pf: [ // lista para penalizar not√≠cias de PF
    'irpf','iptu','ipva','pessoa f√≠sica',
    'imposto de renda pessoa f√≠sica','cpf','mei'
  ]
};

// === Fun√ß√µes auxiliares ===
const norm = (s='') =>
  String(s).toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/https?:\/\/\S+/g,' ')
    .replace(/[^\p{L}\p{N}\s]/gu,' ')
    .replace(/\s+/g,' ')
    .trim();

const tokens = s => new Set(norm(s).split(' ').filter(t => t.length > 2));

const tokenSimilarity = (a,b) => {
  const A = tokens(a), B = tokens(b);
  const inter = [...A].filter(t => B.has(t)).length;
  return inter / (Math.max(A.size, B.size) || 1);
};

const hasAny = (text, list) => {
  const t = norm(text);
  return list.some(k => t.includes(norm(k)));
};

const hoursSince = (d) => {
  if (!d) return 1e9;
  const ts = new Date(d).getTime();
  return isNaN(ts) ? 1e9 : (Date.now() - ts) / 36e5;
};

const recencyMultiplier = (h) => {
  const lambda = Math.log(2) / DECAY_HALF_LIFE_H;
  return Math.exp(-lambda * h);
};

const scoreItem = (j) => {
  const title = j.title || '';
  const body = j['content:encoded'] || j.content || '';
  const snippet = j.contentSnippet || '';
  const cats = Array.isArray(j.categories)
    ? j.categories.map(c => typeof c === 'string' ? c : JSON.stringify(c)).join(' ')
    : (typeof j.categories === 'string' ? j.categories : '');
  const text = `${title} ${snippet} ${body} ${cats}`;

  let score = 0;
  const forte = hasAny(text, KW.forte);
  const contexto = hasAny(text, KW.contextoEco);

  if (forte) score += 40;
  if (hasAny(text, KW.medio)) score += 20;
  if (hasAny(text, KW.brasil)) score += 10;
  if (hasAny(text, KW.fracos)) score -= 50;
  if (hasAny(text, KW.pf)) score -= 40; // penaliza PF

  if (forte && contexto) score += 30;

  const hasNumber = /\b\d+([.,]\d+)?\b/.test(text);
  const hasPercent = /%\b/.test(text);
  if (contexto && hasPercent) score += 15;
  else if (contexto && hasNumber) score += 5;

  score *= recencyMultiplier(hoursSince(j.isoDate || j.pubDate));
  return Math.max(0, Math.min(100, Math.round(score)));
};

// === Pipeline ===
const seenTitles = [];
const scoredItems = [];

for (const item of items) {
  const j = { ...item.json };
  const s = scoreItem(j);

  let dup = false;
  for (const prevTitle of seenTitles) {
    if (tokenSimilarity(prevTitle, j.title || '') >= DEDUPE_SIMILARITY) {
      dup = true; break;
    }
  }
  if (dup) continue;

  if (s >= THRESHOLD) {
    seenTitles.push(j.title || '');
    if (KEEP_SCORE_IN_OUTPUT) j._score = s;
    scoredItems.push({ json: j, _score: s });
  }
}

scoredItems.sort((a,b) => b._score - a._score);
return scoredItems.slice(0, MAX_ITENS).map(i => ({ json: i.json }));

// Coloque os poss√≠veis campos que podem trazer a imagem direto do RSS
const possiveisCampos = [
  $json['media:content']?.url,
  $json['enclosure']?.url,
  $json['image'],
