const raw = $json.output;
const parsed = JSON.parse(raw); // Isso vira um array com 1 objeto

// Retorna o primeiro e único objeto do array
return {
  json: parsed[0]
};

const items = $input.all();
if (!items.length) return [];

const histItem = items[items.length - 1];
const historico = (histItem?.json?.historico ?? '');

// opcional: corta pra evitar prompt gigante
const hist = historico.length > 15000 ? historico.slice(0,15000) : historico;

// tudo menos o último (que é o histórico)
const noticias = items.slice(0, -1);

return noticias.map(i => ({
  json: { ...i.json, historico: hist }
}));

const DIAS = 3;
const corte = new Date(); corte.setDate(corte.getDate() - DIAS);

function parseBR(v){
  if (!v) return NaN;
  if (v instanceof Date) return v;
  const m = String(v).trim().match(/^(\d{2})\/(\d{2})\/(\d{4})$/);
  if (!m) return NaN;
  const [_, dd, mm, yyyy] = m.map(Number);
  return new Date(yyyy, mm-1, dd);
}

// filtra últimos DIAS
const recentes = $input.all().filter(i=>{
  const j = i.json||{};
  const raw = j.data_processamento || j.data_proce || j.data_proces || j.data;
  const d = parseBR(raw);
  return d instanceof Date && !isNaN(d) && d >= corte && (j.status||'').toLowerCase()==='aprovado';
});

// monta string
let historico = recentes.map(i=>{
  const j=i.json||{};
  return `ID:${j.noticia_id} | ${j.titulo||''} | ${j.legenda||''}`;
}).join('\n');

// limita tamanho (evita prompt gigante)
if (historico.length > 30000) historico = historico.slice(0,30000);

// retorna **um único item**
return [{ json: { historico } }];

// Executar: Run Once for Each Item
const i = $itemIndex;

// 1) Buscar dados do Sheets alinhando pelo índice do item atual
const sheetItems = $items('base_noticias');   // <- pega TODOS os itens do nó
const legenda = sheetItems?.[i]?.json?.legenda || '';
const param_2 = sheetItems?.[i]?.json?.param_2 || null;  // 🆕 novo campo
const param_3 = sheetItems?.[i]?.json?.param_3 || null;  // 🆕 novo campo
const param_4 = sheetItems?.[i]?.json?.param_4 || null;  // 🆕 novo campo
const imagem_url = sheetItems?.[i]?.json?.imagem_url || null;  // 🆕 novo campo

// 2) Pegar o .txt baixado do Drive (base64 do ARQUIVO .txt)
const txtB64 = $input.item?.binary?.data?.data;
if (!txtB64) {
  throw new Error('Binário do .txt não encontrado no node "Download file".');
}

// 3) .txt (base64) -> texto UTF-8 -> que contém o base64 da IMAGEM
const content = Buffer.from(txtB64, 'base64').toString('utf8').trim();

// 4) Se vier em dataURI, remover prefixo e capturar MIME
const m = content.match(/^data:([^;]+);base64,(.+)$/i);
let mimeType = m ? m[1] : null;
let base64 = (m ? m[2] : content).replace(/\s+/g, ''); // remove quebras/espaços

// 5) Detectar MIME pelos "magic bytes" caso não tenha
const buf = Buffer.from(base64, 'base64');
if (!mimeType) {
  const sig = buf.slice(0, 4).toString('hex');
  if (sig.startsWith('ffd8ff')) mimeType = 'image/jpeg';
  else if (sig === '89504e47') mimeType = 'image/png';
  else if (sig.startsWith('47494638')) mimeType = 'image/gif';
  else if (sig.startsWith('52494646')) mimeType = 'image/webp';
  else mimeType = 'application/octet-stream';
}

// 6) Retornar o que outros nós vão usar
return {
  json: {
    noticiaIndex: i,
    legenda,
    param_2,    // 🆕 adicionado
    param_3,    // 🆕 adicionado
    param_4,    // 🆕 adicionado
    imagem_url,
    mimeType,
    base64
  }
};

// Normaliza o telefone do item atual
const raw = $json.telefone || "";
let clean = String(raw).replace(/\D+/g, ""); // mantém só os dígitos

if (clean.length === 11) {
  clean = "55" + clean; // adiciona DDI se faltar
} else if (clean.length === 13 && clean.startsWith("55")) {
  // já está ok
}

// Retorna o item original + o campo novo
return {
  json: {
    ...$json,
    telefoneAjustado: clean
  }
};

// 1️⃣ Captura todas as mensagens do Supabase
const mensagens = $items("Get many rows");

// 2️⃣ Captura todos os números existentes no Code4 (para garantir que ninguém fique de fora)
const baseNumeros = $items("Code4").map(item => String(item.json.telefoneAjustado || "").replace(/\D+/g, ""));

// 3️⃣ Pega a data de hoje em formato YYYY-MM-DD
const hoje = new Date().toISOString().slice(0, 10);

// 4️⃣ Cria um dicionário para contar mensagens por telefone
const contagem = {};

for (const item of mensagens) {
  const msg = item.json;
  const telefone = String(msg.telefone || "").replace(/\D+/g, "");
  const dataMsg = msg.created_at ? msg.created_at.slice(0, 10) : "";

  // Se for de hoje, incrementa a contagem
  if (dataMsg === hoje && telefone) {
    contagem[telefone] = (contagem[telefone] || 0) + 1;
  }
}

// 5️⃣ Garante que todos os números da base venham com valor (0 se não tiver mensagens hoje)
for (const tel of baseNumeros) {
  if (!contagem[tel]) contagem[tel] = 0;
}

// 6️⃣ Transforma o dicionário em array (como uma "tabela" pra merge)
const resultado = Object.entries(contagem).map(([telefone, mensagens_hoje]) => ({
  json: { telefone, mensagens_hoje }
}));

return resultado;

// Cria um mapa para controlar quantas vezes cada telefone aparece
const contador = {};

// Percorre todos os itens recebidos (Code7 está antes do Loop)
return $input.all().map(item => {
  const json = item.json;
  const telefone = json.telefoneAjustado;

  // Valor inicial vindo do Supabase
  const mensagensHoje = json.mensagens_hoje || 0;

  // Se o telefone ainda não foi visto neste lote, inicia com o valor atual do Supabase
  if (!contador[telefone]) {
    contador[telefone] = mensagensHoje;
  }

  // Incrementa 1 para esta ocorrência
  contador[telefone]++;

  // Retorna o item com o novo total
  return {
    json: {
      ...json,
      mensagens_hoje: contador[telefone],
    },
  };
});
