// === INPUTS ===
const filtros = $json;
const rubricas = $json.parametros_folha?.rubricas || [];

// === LÓGICA DE PERÍODO ===
// Se usuário pediu um mês específico, usamos +/- 2 meses (total 3 meses)
// Exemplo: perguntou “2025-09” → puxa “2025-07” a “2025-09”
const now = new Date();
let mesInicio, mesFim;

if (filtros.mes) {
  const [ano, mes] = filtros.mes.split('-').map(Number);
  const dataRef = new Date(ano, mes - 1, 1);
  const inicio = new Date(dataRef);
  inicio.setMonth(inicio.getMonth() - 2);
  const fim = new Date(dataRef);
  mesInicio = `${inicio.getFullYear()}-${String(inicio.getMonth() + 1).padStart(2, '0')}`;
  mesFim = `${fim.getFullYear()}-${String(fim.getMonth() + 1).padStart(2, '0')}`;
} else {
  // Se não foi informado mês → usa os 3 últimos meses
  const fim = new Date(now);
  const inicio = new Date(now);
  inicio.setMonth(inicio.getMonth() - 2);
  mesInicio = `${inicio.getFullYear()}-${String(inicio.getMonth() + 1).padStart(2, '0')}`;
  mesFim = `${fim.getFullYear()}-${String(fim.getMonth() + 1).padStart(2, '0')}`;
}

// === BASE DA QUERY ===
let query = `
SELECT TOP 1000
  REPLACE(REPLACE(REPLACE(c.[CPF], '.', ''), '-', ''), ' ', '') AS CPF_Limpo,
  c.[Nome], c.[Cargo], c.[Departamento], c.[Centro_de_Custo],
  c.[Salário], c.[Vale_Refeição], c.[Nome_da_Empresa], c.[Área],
  r.[Código_Rubrica], r.[Descrição_Rubrica], r.[Descrição_Natureza_Rubrica],
  r.[Valor], r.[Mes_ref_folha]
FROM [RhTech_fake_Colaboradores] c
LEFT JOIN [RhTech_fake_Rubricas] r
  ON REPLACE(REPLACE(REPLACE(c.[CPF], '.', ''), '-', ''), ' ', '') = 
     REPLACE(REPLACE(REPLACE(r.[CPF], '.', ''), '-', ''), ' ', '')
WHERE 1=1
`;

// === FILTROS DINÂMICOS ===
if (filtros.tipo_custo && filtros.tipo_custo !== 'todas') {
  const descricoes = rubricas
    .filter(r => r.tipo_custo === filtros.tipo_custo)
    .map(r => `'${r.Descrição_Natureza_Rubrica.replace(/'/g, "''")}'`);
  if (descricoes.length) {
    query += ` AND r.[Descrição_Natureza_Rubrica] IN (${descricoes.join(',')})`;
  }
}

if (filtros.area) query += ` AND c.[Área] = '${filtros.area.replace(/'/g, "''")}'`;
if (filtros.departamento) query += ` AND c.[Departamento] = '${filtros.departamento.replace(/'/g, "''")}'`;
if (filtros.cargo) query += ` AND c.[Cargo] = '${filtros.cargo.replace(/'/g, "''")}'`;
if (filtros.centro_de_custo) query += ` AND c.[Centro_de_Custo] = '${filtros.centro_de_custo.replace(/'/g, "''")}'`;
if (filtros.empresa) query += ` AND c.[Nome_da_Empresa] = '${filtros.empresa.replace(/'/g, "''")}'`;

// === FILTRO TEMPORAL ===
query += ` AND r.[Mes_ref_folha] BETWEEN '${mesInicio}' AND '${mesFim}'`;

// === FINAL ===
query += ` ORDER BY c.[Nome], r.[Mes_ref_folha];`;

return [{ json: { query } }];

// --- Captura dados das planilhas (vindos dos nodes anteriores)
const parametros_folha = {
  rubricas: $('rubricas').all().map(item => item.json),
  empresas: $('empresas').all().map(item => item.json),
  areas: $('areas').all().map(item => item.json),
  departamentos: $('departamentos').all().map(item => item.json),
  centro_de_custo: $('centro_de_custo').all().map(item => item.json),
  cargos: $('cargos').all().map(item => item.json)
};

// --- Captura a pergunta (de Edit Fields ou Trigger)
let pergunta = null;

try {
  pergunta =
    $('Edit Fields').first().json.pergunta ||
    $('trigger').first().json.pergunta ||
    null;
} catch (e) {
  pergunta = null;
}

// --- Retorna JSON consolidado
return [
  {
    json: {
      pergunta,
      parametros_folha
    }
  }
];
