// === INPUT ===
let chart = $json;

// Se vier como string dentro de "output", faz o parse
if (typeof chart.output === 'string') {
  try {
    chart = JSON.parse(chart.output);
  } catch (e) {
    throw new Error('❌ Falha ao converter chart.output para JSON: ' + e.message);
  }
}

// === BASE CONFIG ===
const baseUrl = 'https://image-charts.com/chart.js/2.8.0';
const size = '700x700'; // formato quadrado (ótimo para WhatsApp)
const background = 'white';

// === HELPERS ===
const colors = [
  'rgba(78,121,167,0.7)',
  'rgba(242,142,43,0.7)',
  'rgba(225,87,89,0.7)',
  'rgba(118,183,178,0.7)',
  'rgba(89,161,79,0.7)',
  'rgba(237,201,73,0.7)',
  'rgba(175,122,161,0.7)',
  'rgba(255,157,167,0.7)',
  'rgba(156,117,95,0.7)',
  'rgba(186,176,171,0.7)',
];

// === MONTA O CONFIG JSON ===
let config;

if (chart.tipo === 'linha') {
  let datasets;

  if (Array.isArray(chart.values) && typeof chart.values[0] === 'object' && chart.values[0].data) {
    // Caso com múltiplas séries
    datasets = chart.values.map((serie, i) => ({
      label: serie.name,
      data: serie.data,
      fill: false,
      backgroundColor: colors[i % colors.length],
      borderColor: colors[i % colors.length].replace('0.7', '1'),
      borderWidth: 2,
      tension: 0.3,
    }));
  } else {
    // Caso simples (apenas uma linha)
    datasets = [{
      label: chart.descricao || 'Valores',
      data: chart.values,
      fill: false,
      backgroundColor: colors[0],
      borderColor: colors[0].replace('0.7', '1'),
      borderWidth: 2,
      tension: 0.3,
    }];
  }

  config = {
    type: 'line',
    data: {
      labels: chart.labels,
      datasets,
    },
    options: {
      responsive: true,
      title: { display: true, text: chart.titulo },
      legend: { position: 'top' },
      elements: { point: { radius: 3 } },
    },
  };
}

else if (chart.tipo === 'pizza') {
  config = {
    type: 'pie',
    data: {
      labels: chart.labels,
      datasets: [{
        data: chart.values,
        backgroundColor: colors.slice(0, chart.labels.length),
        borderColor: 'white',
        borderWidth: 2,
      }],
    },
    options: {
      responsive: true,
      title: { display: true, text: chart.titulo },
      legend: { position: 'right' },
    },
  };
}

else if (chart.tipo === 'horizontal') {
  config = {
    type: 'horizontalBar',
    data: {
      labels: chart.labels,
      datasets: [{
        label: chart.descricao || 'Valores',
        data: chart.values,
        backgroundColor: colors.slice(0, chart.labels.length),
        borderColor: colors.slice(0, chart.labels.length).map(c => c.replace('0.7', '1')),
        borderWidth: 1,
      }],
    },
    options: {
      responsive: true,
      legend: { position: 'right' },
      title: { display: true, text: chart.titulo },
      elements: { rectangle: { borderWidth: 2 } },
    },
  };
}

else {
  // barras verticais padrão
  config = {
    type: 'bar',
    data: {
      labels: chart.labels,
      datasets: [{
        label: chart.descricao || 'Valores',
        data: chart.values,
        backgroundColor: colors.slice(0, chart.labels.length),
        borderColor: colors.slice(0, chart.labels.length).map(c => c.replace('0.7', '1')),
        borderWidth: 1,
      }],
    },
    options: {
      responsive: true,
      legend: { position: 'top' },
      title: { display: true, text: chart.titulo },
    },
  };
}

// === FINAL URL ===
const jsonConfig = encodeURIComponent(JSON.stringify(config));
const url = `${baseUrl}?bkg=${background}&c=${jsonConfig}`;

// === RETURN ===
return [{
  json: {
    url,
    chart,
  }
}];

const binaryData = $input.item.binary?.["data"];

if (!binaryData || !binaryData.data) {
  throw new Error("Campo 'data' não encontrado no binário.");
}

const base64 = Buffer.from(binaryData.data, 'base64').toString("base64");

return {
  json: {
    base64Image: base64
  }
};
