const now = new Date();

return items.flatMap(disparo => {
  const status = disparo.json.status;
  const numeroDisparo = disparo.json.qtd_disparo || 0;
  const ultimoDisparo = disparo.json.ultimo_disparo_em ? new Date(disparo.json.ultimo_disparo_em) : null;
  const dataEvento = disparo.json.data_evento ? new Date(disparo.json.data_evento) : null;
  const confirmado = disparo.json.confirmado === 'sim';

  let deveDisparar = false;
  let tipoDisparo = '';

  // LÓGICA PARA CONVITES (status: agendado/convidado)
  if (status === 'agendado' && numeroDisparo === 0) {
    // Primeiro disparo - na data agendada
    const dataDisparo = new Date(disparo.json.data_hora);
    deveDisparar = dataDisparo <= now;
    tipoDisparo = 'primeiro_convite';
  }
  else if (status === 'convidado' && numeroDisparo === 1) {
    // Segundo disparo - 1 dia depois do primeiro
    const dataProximoDisparo = new Date(ultimoDisparo);
    dataProximoDisparo.setDate(dataProximoDisparo.getDate() + 1);
    deveDisparar = dataProximoDisparo <= now;
    tipoDisparo = 'segundo_convite';
  }
  else if (status === 'convidado' && numeroDisparo === 2) {
    // Terceiro disparo - 1 dia depois do segundo  
    const dataProximoDisparo = new Date(ultimoDisparo);
    dataProximoDisparo.setDate(dataProximoDisparo.getDate() + 1);
    deveDisparar = dataProximoDisparo <= now;
    tipoDisparo = 'terceiro_convite';
  }

  // LÓGICA PARA APROVADOS CONFIRMADOS
  else if (status === 'aprovado' && confirmado && dataEvento) {
    // Disparo 1 dia antes do evento
    const dataDisparo = new Date(dataEvento);
    dataDisparo.setDate(dataDisparo.getDate() - 1);
    deveDisparar = dataDisparo <= now;
    tipoDisparo = 'lembrete_evento';
  }

  return deveDisparar ? [{
    json: {
      ...disparo.json,
      _tipo_disparo: tipoDisparo,
      _deve_incrementar: tipoDisparo.includes('convite')
    }
  }] : [];
});

const item = items[0].json;
const tipoDisparo = item._tipo_disparo;
const deveIncrementar = item._deve_incrementar;

let novoStatus = item.status;
let numeroDisparo = item.numero_disparo || 0;

if (deveIncrementar) {
  numeroDisparo += 1;

  // Atualiza status baseado no número do disparo
  if (numeroDisparo === 1) {
    novoStatus = 'convidado'; // Primeiro disparo concluído
  } else if (numeroDisparo === 2) {
    novoStatus = 'convidado'; // Segundo disparo, ainda permite próximo
  } else if (numeroDisparo === 3) {
    novoStatus = 'limiteConvite'; // Após o terceiro disparo não permite mais disparos
  }
}

// Para lembrete_evento, não muda status nem incrementa
if (tipoDisparo === 'lembrete_evento') {
    novoStatus = 'finalizado'; 
}

return [{
  json: {
    disparo_id: item.disparo_id,
    novo_status: novoStatus,
    novo_numero_disparo: numeroDisparo,
    // Mantém outros campos necessários para o fluxo
    telefone: item.telefone,
    nome: item.nome
  }
}];

// Entradas: Supabase (leads) e Sheets (blacklist)
const leads = items;
const blacklist = $items('Verifica opt-out', 0, 0);

// --- Função robusta para normalizar telefone ---
function normalizePhone(phone) {
  if (!phone) return '';

  let clean = String(phone).replace(/\D/g, ''); // remove tudo que não é número

  // Remove prefixo internacional 55 se existir
  if (clean.startsWith('55')) clean = clean.slice(2);

  // Se tiver mais de 11 dígitos (erro comum em planilhas), pega só os últimos 11
  if (clean.length > 11) clean = clean.slice(-11);

  // Se tiver menos de 11 e mais de 8 (fixo sem DDD), ignora
  if (clean.length < 10) return '';

  // Corrige se for 10 dígitos (sem o 9)
  if (clean.length === 10) {
    clean = clean.slice(0, 2) + '9' + clean.slice(2);
  }

  return clean;
}

// --- Cria lista de bloqueados normalizados ---
const blacklistPhones = blacklist
  .map(b => normalizePhone(b.json.Telefone || b.json.telefone))
  .filter(Boolean);

// --- Filtra os leads que NÃO estão na blacklist ---
const filteredLeads = leads.filter(l => {
  const leadPhone = normalizePhone(l.json.telefone);
  const isBlocked = blacklistPhones.includes(leadPhone);
  return leadPhone && !isBlocked;
});

// --- Logs úteis para debug ---
console.log('Blacklisted phones:', blacklistPhones);
console.log('Total leads recebidos:', leads.length);
console.log('Leads após filtro:', filteredLeads.length);

return filteredLeads;

// Code node — Mode: Run Once for All Items

function normalizeTelefone(raw) {
  if (raw == null) return "";
  let d = String(raw).replace(/\D/g, "").replace(/^0+/, "");
  return d ? (d.startsWith("55") ? d : "55" + d) : "";
}

function stripBOM(buf) {
  return (buf.length >= 3 && buf[0] === 0xEF && buf[1] === 0xBB && buf[2] === 0xBF)
    ? buf.slice(3)
    : buf;
}

function fixPngSignature(buf) {
  // PNG correto: 89 50 4E 47 0D 0A 1A 0A
  if (buf.length >= 7 &&
      buf[0] === 0x50 && buf[1] === 0x4E && buf[2] === 0x47 && // "PNG"
      buf[3] === 0x0D && buf[4] === 0x0A && buf[5] === 0x1A && buf[6] === 0x0A) {
    // faltou 0x89 antes de "PNG"
    return Buffer.concat([Buffer.from([0x89]), buf]);
  }
  return buf;
}

async function getBinaryWithHeaders(url) {
  try {
    const res = await this.helpers.httpRequest({
      method: 'GET',
      url,
      headers: { 'User-Agent': 'Mozilla/5.0' },
      responseFormat: 'arraybuffer',
      returnFullResponse: true,
    });
    return { buf: Buffer.from(res.data ?? res), mime: res.headers?.['content-type'] || "" };
  } catch {
    const res = await this.helpers.request({
      method: 'GET',
      url,
      headers: { 'User-Agent': 'Mozilla/5.0' },
      json: false,
      encoding: null,
      resolveWithFullResponse: true,
    });
    return { buf: Buffer.from(res.body), mime: res.headers?.['content-type'] || "" };
  }
}

return await Promise.all(items.map(async (item) => {
  const mensagem      = item.json?.mensagem;
  const remetente     = item.json?.remetente;
  const nome          = item.json?.nome;
  const telefone      = normalizeTelefone(item.json?.telefone);
  const tipo_midia    = (item.json?.tipo_midia || "").toLowerCase();
  const template_name = item.json?.template_name || "";
  const _tipo_disparo = item.json?._tipo_disparo;
  const _deve_incrementar = item.json?._deve_incrementar;
  const disparo_id = item.json?.disparo_id;
  // Novos parâmetros
  const param_1 = item.json?.param_1 || "";
  const param_2 = item.json?.param_2 || "";
  const param_3 = item.json?.param_3 || "";
  const param_4 = item.json?.param_4 || "";
  const param_5 = item.json?.param_5 || "";

  // Novo campo imagem_url
  const imagem_url = (item.json?.imagem_url || "").toString().trim();

  let base64 = "";
  let mime = "";

  if (imagem_url) {
    try {
      let res = await getBinaryWithHeaders.call(this, imagem_url);
      let buf = Buffer.from(res.buf);
      mime = res.mime || "";

      buf = stripBOM(buf);

      // Corrigir assinatura se for imagem PNG
      const isPngHint = (mime && mime.includes('png')) ||
                        (buf.length >= 3 && buf[0] === 0x50 && buf[1] === 0x4E && buf[2] === 0x47);
      if (tipo_midia === "imagem" && isPngHint && buf.length >= 7) {
        buf = fixPngSignature(buf);
      }

      base64 = buf.toString('base64');
    } catch {
      base64 = "";
    }
  }

  return {
    json: {
      mensagem,
      remetente,
      nome,
      telefone,
      tipo_midia,
      mime,
      base64,
      imagem_url,
      template_name,
      param_1,
      param_2,
      param_3,
      param_4,
      param_5,
      _tipo_disparo,
      _deve_incrementar,
      disparo_id
    }
  };
}));
