// === Par√¢metros ===
const THRESHOLD = 80;           // m√≠nimo para aprovar
const MAX_ITENS = 30;           // m√°ximo a retornar
const DECAY_HALF_LIFE_H = 48;   // meia-vida da rec√™ncia (horas)
const DEDUPE_SIMILARITY = 0.85; // similaridade p/ dedupe (0..1)
const KEEP_SCORE_IN_OUTPUT = false; // true s√≥ p/ debug

// === Dicion√°rios de relev√¢ncia (mercado imobili√°rio) ===
const KW = {
  forte: [
    // Termos centrais do setor
    'im√≥vel','imoveis','imovel','imobiliario','mercado imobiliario',
    'apartamento','apartamentos','casa','casas',
    'condominio','condominios','empreendimento','empreendimentos',
    'loteamento','lan√ßamento','constru√ß√£o','obra',
    'aluguel','alugu√©is','loca√ß√£o','comprar im√≥vel','venda de im√≥veis',
    'financiamento imobiliario','credito imobiliario','hipoteca',
    'sbpe','fgts','carta de cr√©dito','cons√≥rcio imobili√°rio',
    'minha casa minha vida','casa verde e amarela','mcmv',
    'taxa de juros','selic','copom','inadimpl√™ncia imobili√°ria',
    'registro de im√≥veis','cart√≥rio de im√≥veis','escritura','averba√ß√£o',
    'iptu','itbi','patrim√¥nio de afeta√ß√£o',
    'incorporadora','construtora','imobili√°ria',
    'fundo imobili√°rio','fii','fiis','lci','lca','cri','cra',
    'cyrela','mrv','tenda','direcional','cury','eztec','loft','quintoandar'
  ],
  medio: [
    'distrato','repasse','estoque de im√≥veis',
    'plano diretor','zoneamento','uso do solo','licenciamento urbano',
    'habite-se','alvar√° de constru√ß√£o','regulariza√ß√£o fundi√°ria',
    'vac√¢ncia','taxa de vac√¢ncia','cap rate','valor venal','valuation imobili√°rio',
    'smart city','cidade inteligente','urbanismo','arquitetura sustent√°vel',
    'sustentabilidade na constru√ß√£o','green building','esg','leed'
  ],
  fracos: [
    'futebol','bbb','celebridade','cantor','cantora','show',
    'meme','tiktok','game','jogo','filme','s√©rie','streaming'
  ],
  brasil: [
    'brasil','s√£o paulo','rio de janeiro','belo horizonte','curitiba','porto alegre',
    'pernambuco','bahia','fortaleza','manaus','goi√¢nia','campinas',
    'caixa','banco do brasil','ita√∫','bradesco','santander','inter','sicredi','sicoob'
  ],
  contextoEco: [
    'mercado de capitais','funding imobili√°rio','capta√ß√£o',
    'juros','selic','copom','pib','infla√ß√£o','ipca','igpm',
    'cr√©dito','investimento imobili√°rio','economia digital',
    'setor de constru√ß√£o','setor imobili√°rio'
  ]
};

// === Fun√ß√µes auxiliares ===
const norm = (s='') =>
  String(s).toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/https?:\/\/\S+/g,' ')
    .replace(/[^\p{L}\p{N}\s]/gu,' ')
    .replace(/\s+/g,' ')
    .trim();

const tokens = s => new Set(norm(s).split(' ').filter(t => t.length > 2));

const tokenSimilarity = (a,b) => {
  const A = tokens(a), B = tokens(b);
  const inter = [...A].filter(t => B.has(t)).length;
  return inter / (Math.max(A.size, B.size) || 1);
};

const hasAny = (text, list) => {
  const t = norm(text);
  return list.some(k => t.includes(norm(k)));
};

const hoursSince = (d) => {
  if (!d) return 1e9;
  const ts = new Date(d).getTime();
  return isNaN(ts) ? 1e9 : (Date.now() - ts) / 36e5;
};

const recencyMultiplier = (h) => {
  const lambda = Math.log(2) / DECAY_HALF_LIFE_H;
  return Math.exp(-lambda * h);
};

const scoreItem = (j) => {
  const title = j.title || '';
  const body = j['content:encoded'] || j.content || '';
  const snippet = j.contentSnippet || '';
  const cats = Array.isArray(j.categories)
    ? j.categories.map(c => typeof c === 'string' ? c : JSON.stringify(c)).join(' ')
    : (typeof j.categories === 'string' ? j.categories : '');
  const text = `${title} ${snippet} ${body} ${cats}`;

  let score = 0;
  const forte = hasAny(text, KW.forte);
  const contexto = hasAny(text, KW.contextoEco);

  if (forte) score += 40;
  if (hasAny(text, KW.medio)) score += 20;
  if (hasAny(text, KW.brasil)) score += 10;
  if (hasAny(text, KW.fracos)) score -= 50;

  if (forte && contexto) score += 30;

  const hasNumber = /\b\d+([.,]\d+)?\b/.test(text);
  const hasPercent = /%\b/.test(text);
  if (contexto && hasPercent) score += 15;
  else if (contexto && hasNumber) score += 5;

  score *= recencyMultiplier(hoursSince(j.isoDate || j.pubDate));
  return Math.max(0, Math.min(100, Math.round(score)));
};

// === Pipeline ===
const seenTitles = [];
const scoredItems = [];

for (const item of items) {
  const j = { ...item.json };
  const s = scoreItem(j);

  let dup = false;
  for (const prevTitle of seenTitles) {
    if (tokenSimilarity(prevTitle, j.title || '') >= DEDUPE_SIMILARITY) {
      dup = true; break;
    }
  }
  if (dup) continue;

  if (s >= THRESHOLD) {
    seenTitles.push(j.title || '');
    if (KEEP_SCORE_IN_OUTPUT) j._score = s;
    scoredItems.push({ json: j, _score: s });
  }
}

scoredItems.sort((a,b) => b._score - a._score);
return scoredItems.slice(0, MAX_ITENS).map(i => ({ json: i.json }));

