const raw = $json.output;
const parsed = JSON.parse(raw); // Isso vira um array com 1 objeto

// Retorna o primeiro e único objeto do array
return {
  json: parsed[0]
};

const items = $input.all();
if (!items.length) return [];

// Pega o primeiro item que tenha histórico
const histItem = items.find(item => item.json?.historico);
const historico = histItem?.json?.historico || '';

// Limita tamanho
const hist = historico.length > 15000 ? historico.slice(0, 15000) : historico;

// Filtra apenas itens que são notícias (tem noticia_id)
const noticias = items.filter(item => item.json?.noticia_id);

// Se não há notícias, retorna vazio
if (!noticias.length) return [];

// Injeta histórico
return noticias.map(noticia => ({
  json: {
    ...noticia.json,
    historico: hist
  }
}));

const DIAS = 3;
const corte = new Date(); 
corte.setDate(corte.getDate() - DIAS);

console.log('=== DEBUG HISTÓRICO ===');
console.log('Data de corte:', corte.toISOString());

function parseBR(v){
  if (!v) return NaN;
  if (v instanceof Date) return v;
  const m = String(v).trim().match(/^(\d{2})\/(\d{2})\/(\d{4})$/);
  if (!m) return NaN;
  const [_, dd, mm, yyyy] = m.map(Number);
  return new Date(yyyy, mm-1, dd);
}

// Debug: ver todas as notícias recebidas
const todasNoticias = $input.all();
console.log('Total notícias recebidas:', todasNoticias.length);

todasNoticias.forEach((noticia, index) => {
  const j = noticia.json || {};
  console.log(`Noticia ${index}:`, {
    id: j.noticia_id,
    status: j.status,
    data: j.data_processamento || j.data_proce || j.data_proces || j.data,
    titulo: j.titulo?.substring(0, 50) + '...'
  });
});

// filtra últimos DIAS
const recentes = todasNoticias.filter(i => {
  const j = i.json || {};
  const raw = j.data_processamento || j.data_proce || j.data_proces || j.data;
  const d = parseBR(raw);
  const statusOk = (j.status || '').toLowerCase() === 'postado';
  const dataOk = d instanceof Date && !isNaN(d) && d >= corte;

  console.log(`Filtro - ID: ${j.noticia_id}, Status: ${j.status}, Data: ${raw}, DataOK: ${dataOk}, StatusOK: ${statusOk}`);

  return dataOk && statusOk;
});

console.log('Notícias recentes encontradas:', recentes.length);

// monta string
let historico = recentes.map(i => {
  const j = i.json || {};
  return `ID:${j.noticia_id} | ${j.titulo || ''} | ${j.legenda || ''}`;
}).join('\n');

console.log('Histórico gerado:', historico.substring(0, 200) + '...');

// limita tamanho (evita prompt gigante)
if (historico.length > 15000) historico = historico.slice(0, 15000);

// Se não há histórico, cria um vazio mas identificável
if (!historico || historico.trim() === '') {
  historico = 'Nenhuma notícia postada nos últimos 3 dias';
  console.log('Histórico VAZIO - usando placeholder');
}

// retorna **um único item**
return [{ 
  json: { 
    historico,
    _debug: {
      total_noticias: todasNoticias.length,
      noticias_recentes: recentes.length,
      data_corte: corte.toISOString()
    }
  } 
}];

// Executar: Run Once for Each Item
const i = $itemIndex;

// 1) Buscar a legenda do Sheets alinhando pelo índice do item atual
const sheetItems = $items('Get row(s) in sheet');   // <- pega TODOS os itens do nó
const legenda = sheetItems?.[i]?.json?.legenda || '';  // usa o i como itemIndex

// 2) Pegar o .txt baixado do Drive (base64 do ARQUIVO .txt)
const txtB64 = $input.item?.binary?.data?.data;
if (!txtB64) {
  throw new Error('Binário do .txt não encontrado no node "Download file".');
}

// 3) .txt (base64) -> texto UTF-8 -> que contém o base64 da IMAGEM
const content = Buffer.from(txtB64, 'base64').toString('utf8').trim();

// 4) Se vier em dataURI, remover prefixo e capturar MIME
const m = content.match(/^data:([^;]+);base64,(.+)$/i);
let mimeType = m ? m[1] : null;
let base64 = (m ? m[2] : content).replace(/\s+/g, ''); // remove quebras/espaços

// 5) Detectar MIME pelos "magic bytes" caso não tenha
const buf = Buffer.from(base64, 'base64');
if (!mimeType) {
  const sig = buf.slice(0, 4).toString('hex');
  if (sig.startsWith('ffd8ff')) mimeType = 'image/jpeg';
  else if (sig === '89504e47') mimeType = 'image/png';
  else if (sig.startsWith('47494638')) mimeType = 'image/gif';
  else if (sig.startsWith('52494646')) mimeType = 'image/webp';
  else mimeType = 'application/octet-stream';
}

// 6) Retornar o que outros nós vão usar
return {
  json: {
    index: i,
    legenda,
    mimeType,
    base64
    // Se o seu node de envio exigir com prefixo:
    // base64WithPrefix: `data:${mimeType};base64,${base64}`
  }
};

const COUNTRY = '55';
const onlyDigits = s => String(s ?? '').replace(/\D/g, '').replace(/^0+/, '');
const toE164NoPlus = d => d ? (d.startsWith(COUNTRY) ? d : COUNTRY + d) : '';
const toJid = e164 => e164 ? `${e164}@s.whatsapp.net` : '';

const out = [];
const seen = new Set();

for (const { json } of items) {
  // prioriza o já normalizado; cai para o bruto se precisar
  const rawDigits = onlyDigits(json.phone_e164 || json.phone_raw);
  if (!rawDigits) continue;

  const e164 = toE164NoPlus(rawDigits); // sem sinal de +
  if (!e164) continue;

  if (seen.has(e164)) continue;
  seen.add(e164);

  const firstName = String(json.name || '').trim().split(/\s+/)[0] || '';

  out.push({
    json: {
      ...json,
      e164,                  // ex: 5511999999999
      jid: toJid(e164),      // ex: 5511999999999@s.whatsapp.net
      firstName
    }
  });
}

return out;

// items[0].json é o objeto "list" do Stripe com data=[...]
const list = items[0].json;
const subs = Array.isArray(list?.data) ? list.data : [];
const now = Math.floor(Date.now() / 1000);

// Validação estrita de telefone BR -> E.164, sem completar DDD.
// Aceita: "+55" + 10 ou 11 dígitos, ou "55" + 10/11 dígitos (sem +).
function toE164StrictBR(raw) {
  const s = String(raw || '').trim();
  if (!s) return { e164: '', ok: false, motivo: 'sem_telefone' };

  // "+55" + 10/11 dígitos
  if (/^\+55\d{10,11}$/.test(s)) return { e164: s, ok: true, motivo: '' };

  // "55" + 10/11 dígitos (sem "+")
  if (/^55\d{10,11}$/.test(s)) return { e164: `+${s}`, ok: true, motivo: '' };

  // qualquer outra coisa = inválido / possivelmente sem DDD
  return { e164: '', ok: false, motivo: 'telefone_mal_formatado_ou_sem_ddd' };
}

const out = [];

for (const s of subs) {
  const c = s.customer || {};
  const phone_raw = c.phone || c.metadata?.whatsapp || '';

  const { e164: phone_e164, ok: phone_ok, motivo: motivoFone } = toE164StrictBR(phone_raw);

  const status_stripe = s.status || '';
  const cancel_at     = s.cancel_at || null;
  const period_end    = s.current_period_end || null;
  const paid_until    = period_end ? new Date(period_end * 1000).toISOString().slice(0, 10) : '';

  // Ativo no Stripe (sem considerar telefone)
  const stripeAtivo = (status_stripe === 'active') &&
                      (!cancel_at) &&
                      (!period_end || period_end >= now);

  // Derivação do status_final
  let status_final = 'inativo';
  let motivo = '';

  if (!phone_raw) {
    status_final = 'validar';
    motivo = 'sem_telefone';
  } else if (!phone_ok) {
    status_final = 'validar';
    motivo = motivoFone; // mal formatado / sem DDD
  } else if (stripeAtivo) {
    status_final = 'ativo';
  } else {
    status_final = 'inativo';
    motivo = status_stripe !== 'active' ? `status_${status_stripe}` :
             cancel_at ? 'cancelado_ao_fim_do_ciclo' :
             (period_end && period_end < now) ? 'periodo_vencido' : '';
  }

  out.push({
    json: {
      name: c.name || '',
      email: c.email || '',
      phone_raw,              // exatamente como veio do Stripe
      phone_e164,             // só se válido sem "chute"
      stripe_customer_id: c.id || '',
      stripe_subscription_id: s.id,
      status_stripe,
      cancel_at,
      paid_until,
      status_final,           // ativo | inativo | validar
      motivo                  // ajuda a entender por que está "validar" ou "inativo"
    }
  });
}

return out;

