const PRICE = {
  input:       1.1   / 1_000_000,
  inputCached: 0.275 / 1_000_000,
  output:      4.4   / 1_000_000,
};

function pickUsage(obj = {}) {
  const v1 = obj.kwargs?.response_metadata?.usage;
  if (v1) {
    return {
      prompt:       v1.prompt_tokens       ?? v1.input_tokens       ?? 0,
      completion:   v1.completion_tokens   ?? v1.output_tokens      ?? 0,
      cached:       v1.prompt_tokens_details?.cached_tokens ?? 0,
    };
  }
  const v2 = obj.usage_metadata;
  if (v2) {
    return {
      prompt:       v2.input_tokens  ?? 0,
      completion:   v2.output_tokens ?? 0,
      cached:       v2.input_token_details?.cache_read ?? 0,
    };
  }
  const v3 = obj.usage;
  if (v3) {
    return {
      prompt:       v3.prompt_tokens ?? v3.input_tokens ?? 0,
      completion:   v3.completion_tokens ?? v3.output_tokens ?? 0,
      cached:       v3.cached_tokens ?? 0,
    };
  }
  return { prompt: 0, completion: 0, cached: 0 };
}

let promptT = 0, completionT = 0, cachedT = 0;
const steps = [];
let summary = '';

for (const item of $input.all()) {
  const conversations = Array.isArray(item.json) ? item.json : [item.json];

  for (const convo of conversations) {
    summary = convo.output ?? summary;

    if (Array.isArray(convo.intermediateSteps)) {
      for (const step of convo.intermediateSteps) {
        if (!step.action) continue;

        const msg = step.action.messageLog?.[0] ?? step.action;
        const u = pickUsage(msg);
        promptT     += u.prompt;
        completionT += u.completion;
        cachedT     += u.cached;

        steps.push({
          tool:        step.action.tool,
          toolInput:   step.action.toolInput,
          observation: step.observation,
        });
      }
    }
  }
}

const nonCached = promptT - cachedT;
const cost = (nonCached * PRICE.input) +
             (cachedT   * PRICE.inputCached) +
             (completionT * PRICE.output);

const result = {
  summary,
  promptTokens:     promptT,
  completionTokens: completionT,
  cachedTokens:     cachedT,
  costUSD:          +cost.toFixed(6),
  totalCostUSD:     +cost.toFixed(6), // opcional: pode remover se quiser evitar repetição
  steps
};

// --- Normalização pro Supabase (opcional se já tiver campos fixos na tabela) ---
