  start: new Date(ev.start.dateTime),
  end: new Date(ev.end.dateTime),
}));

const intervaloMin = agenda.timeBetweenMeetingsMinutes;
const diasSemana = { DOM: 0, SEG: 1, TER: 2, QUA: 3, QUI: 4, SEX: 5, SAB: 6 };

// TZ e string do dia corrente (YYYY-MM-DD) no fuso da agenda
const tz = agenda.timezone || "America/Sao_Paulo";
const hojeStr = new Date().toLocaleString("sv-SE", { timeZone: tz }).slice(0,10);

// Fun√ß√£o auxiliar: pr√≥xima data para um dia da semana (pode retornar hoje)
function getProximaData(dia) {
  const hoje = new Date();
  let data = new Date(hoje);
  while (data.getDay() !== diasSemana[dia]) {
    data.setDate(data.getDate() + 1);
  }
  return data;
}

// === Gera√ß√£o dos slots dispon√≠veis ===
let slotsLivres = [];

for (const dia of agenda.schedule) {
  if (!dia.available) continue;

  const dataAlvo = getProximaData(dia.day);

  // Garante que hours seja sempre array
  const faixas = Array.isArray(dia.hours) ? dia.hours : [dia.hours];

  for (const faixa of faixas) {
    const [hIni, mIni] = faixa.after.split(":").map(Number);
    const [hFim, mFim] = faixa.before.split(":").map(Number);

    const inicioDia = new Date(dataAlvo);
    inicioDia.setHours(hIni, mIni, 0, 0);

    const fimDia = new Date(dataAlvo);
    fimDia.setHours(hFim, mFim, 0, 0);

    let current = new Date(inicioDia);

    while (current < fimDia) {
      const next = new Date(current.getTime() + intervaloMin * 60000);
      if (next > fimDia) break;

      // Exclui qualquer slot do dia corrente (no fuso da agenda)
      const currentDiaStr = current.toLocaleString("sv-SE", { timeZone: tz }).slice(0,10);
      if (currentDiaStr === hojeStr) { current = next; continue; }

      // Checa conflito com eventos
      const ocupado = eventos.some(ev => current < ev.end && next > ev.start);

      if (!ocupado) {
        slotsLivres.push({
          day: dia.day,
          start: current.toLocaleString("sv-SE", { timeZone: tz }).replace(" ", "T") + ":00.000Z",
          end:   next.toLocaleString("sv-SE",   { timeZone: tz }).replace(" ", "T") + ":00.000Z",
        });
      }

      current = next;
    }
  }
}

return slotsLivres;

