    ...(slotsPorDia[solicitadoDia]?.slice(0, 2) || []),
    ...(slotsPorDia[next]?.slice(0, 2) || []),
  ];

  // Se ainda n√£o tiver 2, expande at√© 7 dias
  let offset = 2;
  while (alternativas.length < 2 && offset <= 7) {
    const before = new Date(`${dia}T00:00:00`); before.setDate(before.getDate() - offset);
    const after  = new Date(`${dia}T00:00:00`); after.setDate(after.getDate() + offset);
    alternativas = [
      ...alternativas,
      ...(slotsPorDia[before.toISOString().split("T")[0]]?.slice(0, 2) || []),
      ...(slotsPorDia[after.toISOString().split("T")[0]]?.slice(0, 2) || []),
    ];
    offset++;
  }

  alternativas = alternativas.slice(0, 6).map(slot => ({
    inicio: { dateTime: toYYYYMMDDTHHMMSS(slot.start), timeZone: tz },
    fim:    { dateTime: toYYYYMMDDTHHMMSS(slot.end),   timeZone: tz }
  }));

  return [{
    json: {
      status: "indisponivel",
      message: `O hor√°rio ${dia} ${horario} n√£o est√° dispon√≠vel.`,
      alternativas
    }
  }];
}
