options.customCss:
:root {
	/* Fontes */
	--font-family: 'Orbitron', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 700;

	/* Font Sizes */
	--font-size-body: 13px;
	--font-size-label: 14px;
	--font-size-header: 24px;

	/* Cores Futuristas Final */
	--color-background: #1f1f23; 
	--color-card-bg: #2b2b31;
	--color-card-border: #3e3e46;
	--color-card-shadow: rgba(0, 255, 231, 0.07);

	--color-header: #ffffff;
	--color-label: #c9c9d1;

	/* Cor do texto digitado nos inputs */
	--color-input-text: #1f1f23;  /* ESCURO */
	--color-input-border: #3e3e46;
	--color-focus-border: #00ffe7;

	/* Botão submit neon claro */
	--color-submit-btn-bg: #00ffe7;
	--color-submit-btn-text: #1f1f23;

	--color-error: #ff4b5c;
	--color-required: #ff00ff;
	--color-clear-button-bg: #3e3e46;
	--color-html-text: #c9c9d1;
	--color-html-link: #00ffe7;
	--color-header-subtext: #7e8186;

	/* Bordas */
	--border-radius-card: 12px;
	--border-radius-input: 8px;
	--border-radius-clear-btn: 50%;

	/* Espaçamento */
	--padding-container-top: 32px;
	--padding-card: 28px;
	--padding-form-input: 14px;

	/* Dimensões */
	--container-width: 500px;
	--submit-btn-height: 50px;
	--checkbox-size: 20px;

	/* Outros */
	--box-shadow-card: 0px 0px 20px 2px rgba(0, 255, 231, 0.05);
	--opacity-placeholder: 0.6;
    /* Estilo exclusivo para dropdowns */
    select {
      background-color: #ffffff;  /* Fundo claro */
      color: #1f1f23;            /* Texto escuro */
      border: 1px solid var(--color-input-border);
      border-radius: var(--border-radius-input);
      padding: 10px;
    }

    select option {
      background-color: #ffffff;  /* Fundo claro nas opções */
      color: #1f1f23;            /* Texto escuro nas opções */
    }

    select:focus {
      outline: none;
      border-color: var(--color-focus-border);
      box-shadow: 0 0 0 2px rgba(0, 255, 231, 0.3);
    } 
}
