options.customCss:
:root {
  /* Neon Colors */
  --chat--color-primary: #ff007a;
  --chat--color-primary-shade-50: #e60070;
  --chat--color-primary-shade-100: #cc0065;
  --chat--color-secondary: #00fff7;
  --chat--color-white: #ffffff;
  --chat--color-light: #1a1a1a;
  --chat--color-light-shade-50: #262626;
  --chat--color-light-shade-100: #333333;
  --chat--color-medium: #555555;
  --chat--color-dark: #0d0d0d;
  --chat--color-disabled: #999999;
  --chat--color-typing: #00fff7;

  /* Font */
  --chat--font-family: 'Orbitron', 'Segoe UI', Roboto, sans-serif;

  /* Window */
  --chat--window--background: var(--chat--color-dark);
  --chat--window--border: 1px solid var(--chat--color-primary-shade-50);
  --chat--window--border-radius: 0.5rem;

  /* Header */
  --chat--header--background: var(--chat--color-dark);
  --chat--header--color: var(--chat--color-primary);
  --chat--heading--font-size: 1.8rem;

  /* Message Area */
  --chat--message--font-size: 1rem;
  --chat--message--padding: 1rem;
  --chat--message--bot--background: #111111;
  --chat--message--bot--color: var(--chat--color-white);
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-dark);
  --chat--message--border-radius: 0.75rem;

  /* Input Area */
  --chat--input--background: #000000;
  --chat--input--text-color: var(--chat--color-white);
  --chat--input--border: 1px solid var(--chat--color-primary-shade-100);
  --chat--input--border-radius: 0.4rem;
  --chat--input--placeholder--font-size: 0.9rem;

  /* Buttons */
  --chat--button--background: var(--chat--color-primary);
  --chat--button--color: var(--chat--color-dark);
  --chat--button--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--background: var(--chat--color-primary);
  --chat--toggle--hover--background: var(--chat--color-primary-shade-50);
  --chat--toggle--active--background: var(--chat--color-primary-shade-100);

  /* Scrollbar (opcional) */
  scrollbar-color: var(--chat--color-primary) var(--chat--color-dark);
  scrollbar-width: thin;
}

/* Extra styling for message container */
.chat-message {
  max-width: 70%;
  border: 1px solid var(--chat--color-primary-shade-50);
  box-shadow: 0 0 10px var(--chat--color-primary);
  transition: all 0.3s ease-in-out;
}
