const binaryData = $input.item.binary?.["imagem_final"];

if (!binaryData || !binaryData.data) {
  throw new Error("Campo 'imagem_final' não encontrado no binário.");
}

const base64 = Buffer.from(binaryData.data, 'base64').toString("base64");

return {
  json: {
    base64Image: base64
  }
};

// Coloque os possíveis campos que podem trazer a imagem direto do RSS
const possiveisCampos = [
  $json['media:content']?.url,
  $json['enclosure']?.url,
  $json['image'],
  $json['og:image']
];
const imagemDireta = possiveisCampos.find(x => !!x);

return {
  ...$json,
  imagem: imagemDireta || '' // Se não tiver, deixa vazio
};

return {
  ...$json,
  original: $json // salva todos os dados da notícia
};

const html = $json.data || $json.body || '';
const ogImageMatch = html.match(/<meta\s+property=["']og:image["']\s+content=["']([^"']+)["']/i);
let imagem = ogImageMatch ? ogImageMatch[1] : '';

if (!imagem) {
  const imgMatch = html.match(/<img[^>]+src=["']([^"']+)["']/i);
  if (imgMatch) {
    imagem = imgMatch[1];
  }
}

// Traz de volta todos os campos da notícia original, mais o campo 'imagem'
return {
  ...($('Backup dados da notícia').item.json.original || {}), // todos os campos originais
  imagem                    // novo campo imagem atualizado
};

