// Captura o valor de stdout do node anterior
const sshOutput = $json["stdout"];

// Extrair as porcentagens de CPU, Memória e HD usando regex
const cpuUsageMatch = sshOutput.match(/CPU: (\d+(\.\d+)?)/);
const memoryUsageMatch = sshOutput.match(/MEMÓRIA: (\d+(\.\d+)?)/);
const hdUsageMatch = sshOutput.match(/HD: (\d+(\.\d+)?)/);

if (cpuUsageMatch && memoryUsageMatch && hdUsageMatch) {
    const cpu = parseFloat(cpuUsageMatch[1]);
    const memoria = parseFloat(memoryUsageMatch[1]);
    const hd = parseFloat(hdUsageMatch[1]);

    // Retornar as porcentagens como um JSON com os itens separados
    return {
        cpu,
        memoria,
        hd
    };
} else {
    throw new Error("Erro: Não foi possível extrair os valores de CPU, Memória e HD");
}
