return items.map(item => {
  const { noticia_id, titulo, legenda, fonte, imagem, arquivo_base64, imagem_url, imagem_url2, status, nota } = item.json;
  const id = item.json.id;

  const data = new Date();
  const data_processamento = data.toLocaleDateString('pt-BR'); // formato: dd/mm/aaaa

  return {
    json: {
      noticia_id,
      data_processamento,
      titulo,
      legenda: legenda.replace(/\\n/g, '\n\n'), // <- agora gera 1 linha em branco entre blocos
      fonte,
      imagem,
      arquivo_base64,
      imagem_url,
      imagem_url2,     // <-- agora vai junto
      status,
      nota
    }
  };
});

const dataAgora = new Date();
const yyyy = dataAgora.getFullYear();
const mm = String(dataAgora.getMonth() + 1).padStart(2, '0');
const dd = String(dataAgora.getDate()).padStart(2, '0');
const hh = String(dataAgora.getHours()).padStart(2, '0');
const min = String(dataAgora.getMinutes()).padStart(2, '0');
const ss = String(dataAgora.getSeconds()).padStart(2, '0');

const base = `noticia_${yyyy}${mm}${dd}_${hh}${min}${ss}`;

// coleta TODAS as download_url (inclui download_url2) de TODOS os itens
function collectDownloadUrls(obj, acc = []) {
  if (!obj || typeof obj !== 'object') return acc;
  for (const [k, v] of Object.entries(obj)) {
    if ((k === 'download_url' || k === 'download_url2') && typeof v === 'string') acc.push(v);
    else if (v && typeof v === 'object') collectDownloadUrls(v, acc);
  }
  return acc;
}

// URLs √∫nicas vindas de todos os itens (ramo img1 + ramo img2)
const urlsAll = Array.from(new Set(items.flatMap(i => collectDownloadUrls(i.json))));

// primeira e segunda URL do lote
const url1 = urlsAll[0] ?? null;
const url2 = urlsAll.find(u => u !== url1) ?? null;

return items.map((item, index) => {
  const sequencial = String(index + 1).padStart(3, '0');
  const noticia_id = `${base}_${sequencial}`;

  // se j√° vier setado no item, respeita; sen√£o usa as do lote
  const imagem_url  = item.json.imagem_url  ?? item.json.download_url  ?? url1;
  const imagem_url2 = item.json.imagem_url2 ?? item.json.download_url2 ?? url2;

  return {
    json: {
      ...item.json,
      noticia_id,
      status: item.json.status ?? 'aguardando',
      arquivo_base64: `${noticia_id}.txt`,
      imagem_url,
      imagem_url2,
    }
  };
});

// === Par√¢metros ===
const THRESHOLD = 80;           // m√≠nimo para aprovar
const MAX_ITENS = 30;           // m√°ximo a retornar
const DECAY_HALF_LIFE_H = 48;   // meia-vida da rec√™ncia (horas)
const DEDUPE_SIMILARITY = 0.85; // similaridade p/ dedupe (0..1)
const KEEP_SCORE_IN_OUTPUT = false; // true s√≥ p/ debug

// === Dicion√°rios de relev√¢ncia ===
const KW = {
  forte: [
    'im√≥vel','imoveis','imovel','imobiliario','imobiliarios',
    'apartamento','apartamentos','casa','casas',
    'aluguel','alugu√©is','locacao','locacoes',
    'compra','compras','venda','vendas',
    'construtora','construtoras','incorporacao','incorporacoes',
    'condominio','condominios','empreendimento','empreendimentos',
    'mercado imobili√°rio','constru√ß√£o','lan√ßamento',
    'financiamento','hipoteca','parcelamento',
    'minha casa minha vida','mcmv','casa verde e amarela','sbpe','fgts',
    'taxa de juros','selic','copom','credito imobiliario','inadimpl√™ncia',
    'cri','cra','lci','lca','cvm','bc','bacen',
    'incorporadora','loteamento','regulariza√ß√£o fundi√°ria',
    'iptu','itbi','itcmd','registro de im√≥veis','cart√≥rio','usucapi√£o',
    'incc','igpm','ipca','poupan√ßa','caderneta',
    'creci','lei do inquilinato','locat√°rio','locador','fiador','garantia',
    'mrv','direcional','tenda','cyrela','cury','eztec','melnick','jhsf',
    'viva real','olx','quintoandar','loft',
    'intelig√™ncia artificial','ia','automa√ß√£o','chatbot',
    'realidade aumentada','ra','realidade virtual','rv','modelagem 3d','digital twin',
    'arquitetura','urbanismo','sustentabilidade','constru√ß√£o modular','prefabricada'
  ],
  medio: [
    'programa habitacional','estoque','distrato','distratos','repasse','repasses',
    'score de cr√©dito','registro eletr√¥nico','onboarding digital',
    'compliance','fraude','due diligence','kpi','okr',
    'benchmark','market share','sazonalidade','ticket m√©dio',
    'taxa de vac√¢ncia','vac√¢ncia','aluguel m√©dio','cap rate','valor venal',
    'plano diretor','zoneamento','uso e ocupa√ß√£o do solo'
  ],
  fracos: [
    'jogos','game','celebridade','famoso','esporte','entretenimento',
    'show','cantor','cantora','filme','s√©rie','bbb','futebol',
    'gadget','smartphone','token','meme'
  ],
  brasil: [
    'brasil','s√£o paulo','rio de janeiro','minas gerais','uberl√¢ndia','goi√°s','bahia',
    'paran√°','santa catarina','rio grande do sul',
    'caixa','bb','ita√∫','bradesco','santander','inter','original','sicredi','sicoob'
  ],
  contextoEco: [
    'selic','copom','taxa de juros','infla√ß√£o','pib','financiamento',
    'mercado imobili√°rio','credito imobiliario','juros','investimento',
    'funding','capta√ß√£o','mercado de capitais','economia'
  ]
};

// === Fun√ß√µes auxiliares ===
const norm = (s='') =>
  String(s).toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g,'')
    .replace(/https?:\/\/\S+/g,' ')
    .replace(/[^\p{L}\p{N}\s]/gu,' ')
    .replace(/\s+/g,' ')
    .trim();

const tokens = s => new Set(norm(s).split(' ').filter(t => t.length > 2));

const tokenSimilarity = (a,b) => {
  const A = tokens(a), B = tokens(b);
  const inter = [...A].filter(t => B.has(t)).length;
  return inter / (Math.max(A.size, B.size) || 1);
};

const hasAny = (text, list) => {
  const t = norm(text);
  return list.some(k => t.includes(norm(k)));
};

const hoursSince = (d) => {
  if (!d) return 1e9;
  const ts = new Date(d).getTime();
  return isNaN(ts) ? 1e9 : (Date.now() - ts) / 36e5;
};

const recencyMultiplier = (h) => {
  const lambda = Math.log(2) / DECAY_HALF_LIFE_H;
  return Math.exp(-lambda * h);
};

const scoreItem = (j) => {
  const title = j.title || '';
  const body = j['content:encoded'] || j.content || '';
  const snippet = j.contentSnippet || '';
  const cats = Array.isArray(j.categories)
    ? j.categories.map(c => typeof c === 'string' ? c : JSON.stringify(c)).join(' ')
    : (typeof j.categories === 'string' ? j.categories : '');
  const text = `${title} ${snippet} ${body} ${cats}`;

  let score = 0;
  const forte = hasAny(text, KW.forte);
  const contexto = hasAny(text, KW.contextoEco);

  if (forte) score += 40;
  if (hasAny(text, KW.medio)) score += 20;
  if (hasAny(text, KW.brasil)) score += 10;
  if (hasAny(text, KW.fracos)) score -= 50;

  if (forte && contexto) score += 30;

  const hasNumber = /\b\d+([.,]\d+)?\b/.test(text);
  const hasPercent = /%\b/.test(text);
  if (contexto && hasPercent) score += 15;
  else if (contexto && hasNumber) score += 5;

  score *= recencyMultiplier(hoursSince(j.isoDate || j.pubDate));
  return Math.max(0, Math.min(100, Math.round(score)));
};

// === Pipeline ===
const seenTitles = [];
const scoredItems = [];

for (const item of items) {
  const j = { ...item.json };
  const s = scoreItem(j);

  let dup = false;
  for (const prevTitle of seenTitles) {
    if (tokenSimilarity(prevTitle, j.title || '') >= DEDUPE_SIMILARITY) {
      dup = true; break;
    }
  }
  if (dup) continue;

  if (s >= THRESHOLD) {
    seenTitles.push(j.title || '');
    if (KEEP_SCORE_IN_OUTPUT) j._score = s;
    scoredItems.push({ json: j, _score: s });
  }
}

scoredItems.sort((a,b) => b._score - a._score);
return scoredItems.slice(0, MAX_ITENS).map(i => ({ json: i.json }));

